import React, { useState, useEffect, useRef } from "react";
import * as d3 from "d3";

const emotionColors = {
  "Joy": "#FFEB3B",
  "Sadness": "#2196F3",
  "Fear": "#9C27B0",
  "Disgust": "#4CAF50",
  "Anger": "#F44336",
  "Surprise": "#FFB322",
  "Neutral": "#9E9E9E"
};

const EmotionAnalysisVisualization = () => {
  const [data, setData] = useState([]);
  const [selectedSentence, setSelectedSentence] = useState(null);
  const [inputText, setInputText] = useState("");
  const [selectedEmotions, setSelectedEmotions] = useState(new Set(Object.keys(emotionColors)));
  const chartRef = useRef(null);
  const steamGraphRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    d3.csv("/backend/emotion_analysis.csv").then(setData);
  }, []);

  const emotions = data.length > 0 ? Object.keys(data[0]).filter(key => key !== "Sentence") : [];

  useEffect(() => {
    if (selectedSentence) drawBarChart();
  }, [selectedSentence]);

  useEffect(() => {
    if (data.length > 0) drawSteamGraph();
  }, [data, selectedEmotions]);

  // ✅ Restore Function: Send Text to Flask for Analysis
  const handleAnalyze = () => {
    fetch("/analyze", { 
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text: inputText })
    })
    .then(res => res.json())
    .then(data => setData(data.results))
    .catch(error => console.error("Error analyzing text:", error));
  };

  // ✅ Restore Function: Upload File to Flask
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    fetch("/upload", {  
      method: "POST",
      body: formData
    })
    .then(res => res.json())
    .then(data => setData(data.results))
    .catch(error => console.error("Error uploading file:", error));
  };

  // ✅ Draw Bar Chart for Selected Sentence
  const drawBarChart = () => {
    const chartDiv = d3.select(chartRef.current);
    chartDiv.html("");

    const margin = { top: 10, right: 30, bottom: 50, left: 50 };
    const width = chartDiv.node().clientWidth - margin.left - margin.right;
    const height = chartDiv.node().clientHeight - margin.top - margin.bottom;

    const svg = chartDiv.append("svg")
      .attr("viewBox", `0 0 ${width + margin.left + margin.right} ${height + margin.top + margin.bottom}`)
      .attr("preserveAspectRatio", "xMidYMid meet")
      .style("width", "100%")
      .style("height", "100%")
      .append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`);

    const x = d3.scaleBand().domain(emotions).range([0, width]).padding(0.2);
    const y = d3.scaleLinear().domain([0, 1]).nice().range([height, 0]);

    svg.append("g").attr("transform", `translate(0,${height})`).call(d3.axisBottom(x));
    svg.append("g").call(d3.axisLeft(y).ticks(5).tickFormat(d => `${d * 100}%`));

    const emotionScores = emotions.map(emotion => ({
      emotion,
      score: +selectedSentence[emotion] || 0
    }));

    svg.selectAll(".bar").data(emotionScores).enter()
      .append("rect")
      .attr("x", d => x(d.emotion))
      .attr("y", d => y(d.score))
      .attr("width", x.bandwidth())
      .attr("height", d => height - y(d.score))
      .style("fill", d => emotionColors[d.emotion]);
  };

  // ✅ Draw Steam Graph (Filtered by Selected Emotions)
  const drawSteamGraph = () => {
    const container = d3.select(steamGraphRef.current);
    container.html("");

    const margin = { top: 10, right: 30, bottom: 50, left: 50 };
    const width = container.node().clientWidth - margin.left - margin.right;
    const height = container.node().clientHeight - margin.top - margin.bottom;

    const svg = container.append("svg")
      .attr("viewBox", `0 0 ${width + margin.left + margin.right} ${height + margin.top + margin.bottom}`)
      .attr("preserveAspectRatio", "xMidYMid meet")
      .style("width", "100%")
      .style("height", "100%")
      .append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`);

    const x = d3.scaleLinear().domain([1, data.length]).range([0, width]);
    const y = d3.scaleLinear().domain([0, 1]).nice().range([height, 0]);

    svg.append("g").attr("transform", `translate(0,${height})`).call(d3.axisBottom(x));
    svg.append("g").call(d3.axisLeft(y).ticks(5).tickFormat(d3.format(".1f")));

    emotions.forEach(emotion => {
      if (selectedEmotions.has(emotion)) {
        const line = d3.line()
          .x((_, i) => x(i + 1))
          .y(d => y(+d[emotion]))
          .curve(d3.curveBasis);

        svg.append("path")
          .datum(data)
          .attr("fill", "none")
          .attr("stroke", emotionColors[emotion])
          .attr("stroke-width", 2)
          .attr("d", line);
      }
    });
  };

  // ✅ Toggle Emotion Visibility in Steam Graph
  const toggleEmotion = (emotion) => {
    const newSelection = new Set(selectedEmotions);
    if (newSelection.has(emotion)) {
      newSelection.delete(emotion);
    } else {
      newSelection.add(emotion);
    }
    setSelectedEmotions(newSelection);
  };

  return (
    <div className="container">
      <div className="left-panel">
        <div className="input-section">
          <textarea 
            value={inputText} 
            onChange={e => setInputText(e.target.value)} 
            placeholder="Enter text here..."
          />
          <div className="button-group">
            <button onClick={handleAnalyze}>Analyze</button>
            <button onClick={() => fileInputRef.current.click()}>Upload</button>
            <input 
              type="file" 
              accept=".txt" 
              ref={fileInputRef} 
              style={{ display: "none" }} 
              onChange={handleFileUpload} 
            />
          </div>
        </div>
      </div>

      <div className="right-panel">
        <div className="graph-section">
          <div id="steamGraph" ref={steamGraphRef}></div>
          <div className="legend">
            {emotions.map(emotion => (
              <span key={emotion} 
                onClick={() => toggleEmotion(emotion)}
                style={{ color: emotionColors[emotion], cursor: "pointer", marginRight: "15px", fontWeight: selectedEmotions.has(emotion) ? "bold" : "normal" }}>
                ⬤ {emotion}
              </span>
            ))}
          </div>
        </div>
        <div className="graph-section">
          <div id="chart" ref={chartRef}></div>
        </div>
      </div>
    </div>
  );
};

export default EmotionAnalysisVisualization;
